mapper.select:
SELECT COUNT(*)
FROM apply
WHERE tutor_id = #{tutorId};

SELECT *
FROM apply
WHERE tutor_id = #{tutorId}
  AND apply_type_id = #{applyTypeId}

SELECT *
FROM apply
WHERE tutor_id = #{tutorId}
  AND apply_type_id = #{applyTypeId}
  AND status = 0;

mapper.update:
UPDATE apply
SET subject = #{applySubject}
WHERE apply_type_id = #{applyId}

mapper.select:
SELECT apply_id
FROM apply
WHERE tutor_id = #{tutorId}
  AND apply_type_id = #{applyTypeId}
  AND status = #{status};

SELECT apply_id
FROM apply
WHERE tutor_id = #{tutorId}
  AND status = #{status};
